mapper.select:
select
  menu_id, parent_id, title, href, perms, icon, target, create_time, modify_time
from u_menu


        and menu_id = #{menuId}


        and parent_id = #{parentId}


        and title = #{title}


        and href = #{href}


        and perms = #{perms}


        and icon = #{icon}


        and target = #{target}


        and create_time = #{createTime}


        and modify_time = #{modifyTime}
