mapper.select:
select
  vehicleinfo_id, vehicle_type, vehicle_seats_number, vehicle_number, vehicle_status, vehicle_affiliation_company, vehicle_affiliation_personal, create_time, modify_time
from car.vehicleinfo


        and vehicleinfo_id = #{vehicleinfoId}


        and vehicle_type = #{vehicleType}


        and vehicle_seats_number = #{vehicleSeatsNumber}


        and vehicle_number = #{vehicleNumber}


        and vehicle_status = #{vehicleStatus}


        and vehicle_affiliation_company = #{vehicleAffiliationCompany}


        and vehicle_affiliation_personal = #{vehicleAffiliationPersonal}


        and create_time = #{createTime}


        and modify_time = #{modifyTime}

select vehicleinfo_id as vehicleinfoId, vehicle_type as vehicleType, vehicle_seats_number as vehicleSeatsNumber, vehicle_number as vehicleNumber, vehicle_status as vehicleStatus, vehicle_affiliation_company as vehicleAffiliationCompany, c.company_name as companyName, vehicle_affiliation_personal as vehicleAffiliationPersonal, u.name as name, v.create_time as createTime, v.modify_time as modifyTime from vehicleinfo v LEFT JOIN company c on v.vehicle_affiliation_company = c.company_id LEFT JOIN u_userinfo u on v.vehicle_affiliation_personal =u.id
and v.vehicle_number= #{vehicleNumber}
and c.company_name = #{companyName}
ORDER BY vehicle_status Desc
